// Validate and enrich employee data
const item = $input.first().json;

// Required fields
const requiredFields = ['firstName', 'lastName', 'email', 'jobTitle', 'department', 'startDate'];
const missingFields = requiredFields.filter(field => !item[field]);

if (missingFields.length > 0) {
  throw new Error(`Missing: ${missingFields.join(', ')}`);
}

// Validate email
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!emailRegex.test(item.email)) {
  throw new Error('Invalid email format');
}

// Format dates
const startDate = new Date(item.startDate);
if (isNaN(startDate.getTime())) {
  throw new Error('Invalid start date');
}

const formatDate = (date) => {
  return date.toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' });
};

item.startDateFormatted = formatDate(startDate);
item.currentDate = formatDate(new Date());

// Generate employee ID
if (!item.employeeId) {
  const year = startDate.getFullYear();
  const random = Math.floor(Math.random() * 10000).toString().padStart(4, '0');
  item.employeeId = `EMP-${year}-${random}`;
}

// Full name
item.fullName = `${item.firstName} ${item.lastName}`;

// Determine employment type defaults
item.employmentType = item.employmentType || 'Full-Time';
item.location = item.location || 'San Francisco, CA';
item.workSchedule = item.workSchedule || 'Monday-Friday, 9:00 AM - 5:00 PM';

// Manager info
item.managerName = item.managerName || 'Department Manager';
item.managerEmail = item.managerEmail || `manager@company.com`;
item.managerPhone = item.managerPhone || '+1 (555) 123-4567';

// HR contact
item.hrContactName = item.hrContactName || 'HR Department';
item.hrContactEmail = item.hrContactEmail || 'hr@mediajde.com';
item.hrContactPhone = item.hrContactPhone || '+1 (555) 987-6543';

// Salary info (if provided)
if (item.salary) {
  item.salary = parseFloat(item.salary);
  item.salaryFormatted = `$${item.salary.toLocaleString('en-US')}`;
}

// Benefits eligibility
item.benefitsEligible = item.employmentType === 'Full-Time';
item.benefitsStartDate = new Date(startDate.getTime() + 30*24*60*60*1000).toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });

// Role-specific categorization
const techRoles = ['Software Engineer', 'Developer', 'Data Scientist', 'IT Specialist', 'DevOps Engineer'];
const managementRoles = ['Manager', 'Director', 'VP', 'Executive'];
const salesRoles = ['Sales', 'Account Executive', 'Business Development'];

item.requiresITEquipment = techRoles.some(role => item.jobTitle.includes(role));
item.requiresManagementTraining = managementRoles.some(role => item.jobTitle.includes(role));
item.requiresSalesTools = salesRoles.some(role => item.jobTitle.includes(role));

// Department-specific info
const departmentInfo = {
  'Engineering': { floor: '3rd Floor', dressCode: 'Casual', parkingSpot: 'Garage Level 2' },
  'Sales': { floor: '2nd Floor', dressCode: 'Business Casual', parkingSpot: 'Garage Level 1' },
  'Marketing': { floor: '4th Floor', dressCode: 'Business Casual', parkingSpot: 'Garage Level 2' },
  'Finance': { floor: '5th Floor', dressCode: 'Business Professional', parkingSpot: 'Garage Level 1' },
  'HR': { floor: '1st Floor', dressCode: 'Business Casual', parkingSpot: 'Garage Level 1' },
  'Operations': { floor: '2nd Floor', dressCode: 'Casual', parkingSpot: 'Garage Level 3' }
};

const deptInfo = departmentInfo[item.department] || { floor: '2nd Floor', dressCode: 'Business Casual', parkingSpot: 'Visitor Parking' };
item.officeFloor = deptInfo.floor;
item.dressCode = deptInfo.dressCode;
item.parkingSpot = deptInfo.parkingSpot;

// Company info
item.companyName = item.companyName || 'Media Jade';
item.companyAddress = item.companyAddress || '456 Company Street, San Francisco, CA 94105';
item.companyPhone = item.companyPhone || '+1 (555) 111-2222';
item.companyWebsite = item.companyWebsite || 'www.mediajde.com';

// Document tracking
item.documentPackId = `PACK-${item.employeeId}-${Date.now()}`;
item.generatedAt = new Date().toISOString();

return { json: item };

// Merge all HTML documents and convert references
const item = $input.first().json;

// Collect all generated HTML documents
const documents = [
  { name: 'Welcome_Letter', html: item.welcomeLetterHtml, order: 1 },
  { name: 'Benefits_Guide', html: item.benefitsGuideHtml, order: 2 },
  { name: 'IT_Setup_Instructions', html: item.itSetupHtml, order: 3 },
  { name: 'Emergency_Contact_Direct_Deposit_Forms', html: item.formsHtml, order: 4 }
];

// Sort by order
documents.sort((a, b) => a.order - b.order);

// Return array of documents for PDF conversion
return documents.map(doc => ({
  json: {
    ...item,
    documentName: doc.name,
    documentHtml: doc.html,
    fileName: `${doc.order}_${doc.name}_${item.employeeId}.pdf`
  }
}));

// Prepare email with all document attachments
const items = $input.all();

if (items.length === 0) {
  throw new Error('No documents to send');
}

// Get employee info from first item
const employeeData = items[0].json;

// Collect all PDF data
const attachments = items.map(item => ({
  name: item.json.fileName,
  data: item.binary?.data?.data || '',
  type: 'application/pdf'
}));

const emailBody = `
Dear ${employeeData.fullName},

Welcome to ${employeeData.companyName}! We're excited to have you join our team as ${employeeData.jobTitle} in the ${employeeData.department} department.

Your start date is ${employeeData.startDateFormatted}. Please find attached your complete onboarding document package, which includes:

1. Welcome Letter - Important first-day information and what to bring
2. Benefits Enrollment Guide - Comprehensive overview of your benefits options
3. IT Setup Instructions - Technology equipment and access details
4. Forms Package - Emergency contact and direct deposit authorization forms

IMPORTANT ACTION ITEMS:
‚Ä¢ Review all attached documents before your start date
‚Ä¢ Complete and bring signed forms on your first day
‚Ä¢ Prepare direct deposit information (voided check or bank letter)
‚Ä¢ List your emergency contacts
‚Ä¢ Note your reporting time and location

Your First Day:
‚Ä¢ Date: ${employeeData.startDateFormatted}
‚Ä¢ Time: 9:00 AM
‚Ä¢ Location: ${employeeData.location}
‚Ä¢ Report to: Reception on ${employeeData.officeFloor}
‚Ä¢ Dress Code: ${employeeData.dressCode}

If you have any questions before your start date, please don't hesitate to contact:

${employeeData.hrContactName}
${employeeData.hrContactEmail}
${employeeData.hrContactPhone}

We look forward to welcoming you to the ${employeeData.companyName} team!

Best regards,
${employeeData.hrContactName}
Human Resources Department
${employeeData.companyName}
`;

return [{
  json: {
    ...employeeData,
    emailTo: employeeData.email,
    emailSubject: `Welcome to ${employeeData.companyName} - Your Onboarding Package`,
    emailBody: emailBody,
    documentCount: attachments.length,
    attachments: attachments
  }
}];

// Log onboarding package in HR system
const item = $input.first().json;

const hrSystemLog = {
  employeeId: item.employeeId,
  fullName: item.fullName,
  email: item.email,
  jobTitle: item.jobTitle,
  department: item.department,
  startDate: item.startDate,
  employmentType: item.employmentType,

  // Onboarding package details
  documentPackId: item.documentPackId,
  documentsGenerated: item.documentCount,
  generatedAt: item.generatedAt,
  sentAt: new Date().toISOString(),

  // Document tracking
  documents: [
    { name: 'Welcome Letter', status: 'Sent', requiresSignature: false },
    { name: 'Benefits Guide', status: 'Sent', requiresSignature: false },
    { name: 'IT Setup Instructions', status: 'Sent', requiresSignature: false },
    { name: 'Emergency Contact Form', status: 'Sent', requiresSignature: true, dueDate: item.startDate },
    { name: 'Direct Deposit Form', status: 'Sent', requiresSignature: true, dueDate: item.startDate }
  ],

  // Completion tracking
  completionStatus: {
    documentsReceived: true,
    formsCompleted: false,
    benefitsEnrolled: false,
    itSetupComplete: false,
    onboardingComplete: false
  },

  // Manager and HR info
  managerName: item.managerName,
  managerEmail: item.managerEmail,
  hrContact: item.hrContactName,

  // Benefits eligibility
  benefitsEligible: item.benefitsEligible,
  benefitsStartDate: item.benefitsStartDate,

  // IT requirements
  requiresITEquipment: item.requiresITEquipment,
  requiresManagementTraining: item.requiresManagementTraining,
  requiresSalesTools: item.requiresSalesTools,

  // API integration
  apiEndpoint: 'https://api.yourhrsystem.com/onboarding',
  apiMethod: 'POST',

  // Activity log
  activityLog: [
    {
      timestamp: new Date().toISOString(),
      action: 'Onboarding Package Generated',
      user: 'System',
      details: `Generated ${item.documentCount} documents for ${item.fullName}`
    },
    {
      timestamp: new Date().toISOString(),
      action: 'Documents Sent',
      user: 'System',
      details: `Sent onboarding package to ${item.email}`
    }
  ]
};

return {
  json: {
    ...item,
    hrSystemLog: hrSystemLog,
    loggedAt: new Date().toISOString()
  }
};

