let input = {
  maxRequests: 0,
  sameDomain: true,
  onlyEmails: false,
  onlyOneEmailPerDomain: false,
  maxDepth: 1,
  pseudoUrls: [".*"],
  proxyConfig: {
    useApifyProxy: true
  },
  considerChildFrames: true
}

const startUrls = []
for (const item of $('Get website URLs from first sheet').all()) {
  const newItem = {}
  newItem.url = item.json.url || item.json.website
  startUrls.push(newItem)
}



return { 
  json: {
    ...input,
    startUrls
  }
}
